test-case.test:
let $params := map {
            "use-character-maps" : true(),
            "indent" : true() 
            }          
            return serialize(., $params)

let $params := map {
            "method" : "xml",
            "indent" : true(),
            "cdata-section-elements" : 
                (QName("http://www.saxonica.com", "a"), QName("http://www.saxonica.com", "b"), QName("", "c"))
            }          
            return serialize(., $params)

let $params := map {
            "method" : "xml",
            "indent" : true(),
            "cdata-section-elements" : 
                [QName("http://www.saxonica.com", "a"), QName("http://www.saxonica.com", "b"), QName("", "c")] 
            }          
            return serialize(., $params)

let $params := map {
            "method" : "xml",
            "indent" : true(),
            "xindent" : true() 
            }
            return serialize(., $params)

let $params := map {
            "method" : "xml",
            "indent" : true(),
            QName("http://vendor.example.com/","xindent") : true() 
            }
            return serialize(., $params)

let $params := map {
            "method" : "xml",
            "indent" : true(),
            "suppress-indentation" : QName("","p") 
            }
            return serialize(., $params)

let $params := map {
            "method" : "xml",
            "cdata-section-elements" : 
            (QName("", "b"), QName("http://www.example.org/ns/p", "b")),
            "suppress-indentation" : QName("", "para")
            }
            return serialize(., $params)

let $params := map {
            "indent" : true(),
            "indent" : true() 
            }
            return serialize(., $params)

let $params := map { 
            "omit-xml-declaration" : false(),
            "standalone" : false() 
            }
            return serialize(., $params)

let $params := map {
            "omit-xml-declaration" : false(),
            "standalone" : true() 
            }
            return serialize(., $params)

let $params := map {
            "omit-xml-declaration" : false(),
            "standalone" : " omit " 
            }
            return serialize(., $params)

let $params := map {
            "method" : "xml",
            "omit-xml-declaration" : true(),
            "item-separator" : "==" 
            }
            return serialize((1 to 4)!text{.}, $params)

let $params := map {
            "method" : "xml",
            "version" : "1.1",
            "omit-xml-declaration" : false(),
            "undeclare-prefixes" : false()
            }
            return serialize(., $params)


let $options := map {
   'use-character-maps' : map {
      'x' : xs:untypedAtomic('j'),
      'm' : <e>so</e>,
      'l' : 'n'
   }
}
return     
   serialize(<e>[xml]</e>, $options)


let $options := map {
   'use-character-maps' : map {
      'x' : xs:untypedAtomic('j'),
      'm' : <e>so</e>,
      'l' : xs:QName('n')
   }
}
return     
   serialize(<e>[xml]</e>, $options)

let $doc := <html><head/><body><p>Hello World!</p></body></html>
let $params := map {
"method" : "html",
"html-version" : 5.0
}       
return serialize($doc, $params)

let $doc := <html><head/><body><p>Hello World!</p></body></html>
let $params := map {
"method" : "html",
"html-version" : 5
}       
return serialize($doc, $params)

let $params := map {
            "method" : "json",
            "indent" : true() 
            },         
            $arg := parse-json('{"abc":true}')
            return serialize($arg, $params)

let $params := map {
            "method" : "json",
            "json-node-output-method" : "xml"
            },
            $s := serialize(comment {" hello world "}, $params)
            return matches($s, '"<!-- hello world -->"')

let $params := map {
            "method" : "json",
            "json-node-output-method" : "xml"
            },
            $s := serialize(array {
            text { "a" },
            processing-instruction {"a"} {"b"},
            comment { "a" },
            element {"a"} {"b"},
            document { element {"a"}{"b"} }
            }, $params)
            return matches($s, '\["a","<\?a b\?>","<!--a-->","<a>b<\\/a>","<a>b<\\/a>"\]')

all-of.assert:
let $xml := json-to-xml($result, map { 'duplicates' : 'retain' })
return
   count($xml/fn:map/fn:number) eq 2

test-case.test:
let $r := serialize(parse-json('[1, 2, 3, "four", true, false, null]'),
            map{'method':'json'}) return translate($r,' 	
', '')

let $r := serialize([number('NaN'), number('INF'), number('-INF')],
            map{'method':'json'}) return translate($r,' 	
', '')
