test-case.test:

for $x in 1 to 100 
group by $key := $x mod 10 
return string(text{$x})


for $x in //employee 
let $key := ($x/@gender = 'male') 
group by $key 
return concat($key, ':',  
       string-join(for $e in $x return $e/@name/string(), ','))

for $x in (true(), "true", xs:QName("true"))
group by $x
return $x
